async function processImage(){const e=imageInput.files[0];if(e){generateBtn.disabled=!0,output.innerHTML='<div class="loading"><div class="loader"></div><p>Extracting text from image...</p></div>';try{const{data:{text:t}}=await Tesseract.recognize(e,"eng");output.innerHTML='<div class="loading"><div class="loader"></div><p>Generating quiz questions...</p></div>';const n=await fetch("http://localhost:5000/api/generate",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({text:t})});if(!n.ok)throw new Error(`Server responded with status: ${n.status}`);const s=await n.json();output.innerHTML=`\n      <div class="mcq-result">\n        <h3>Your Sample MCQs</h3>\n        <pre>${s.questions}</pre>\n      </div>\n    `,document.getElementById("cta").style.display="block",output.scrollIntoView({behavior:"smooth"})}catch(e){output.innerHTML=`<p>Error: ${e.message}</p>`,console.error("Error processing image:",e)}finally{generateBtn.disabled=!1}}}